function playDrum(time, dtype) {
  switch( dtype) {
    case 1:
      drum.play(time,1, .5);
      break;
    case 2:
      snare.play(time,1,.5);
      break;
    case 3:
      tic.play(time,1,0.6);
      break;


  }
}

function playNote(time, note) {
  console.log( time,note);
  setTimeout( () => { tic.play(time,1,0.6); osc.amp(0.6); osc.freq(midiToFreq(ansi_to_midi(note))) }, time);
  setTimeout( () => { osc.amp(0); }, time + (120/bpm)*1000 );
  //setTimeout(function() { osc.fade(0,0.2); }, 5000);
  background(random(255), random(255), 255);
}
